(3781, 1, '2014-01-14 17:33:43', '2014-01-14 17:33:43', '[one_half]\n\n<strong>Right on the ground, for the children of the DRC.</strong>\n\nSpecialists, technical and financial partners, working partners, volunteers... in this section you will find a list of everyone who has committed day after day to improving the situation of children in the DRC.\n\nDon''t hesitate to get in touch with your ideas, to share our articles and to follow us on social networks so that as many people as possible can find out about these children and the difficulties they face.\n\n[/one_half]\n\n[one_half_last]\n\n[accordion]\n\n[section title="Who are we?"]<a href="/?page_id=1716">Pona Bana is the Blog of UNICEF''s team in DRC. Find a brief summary of our vision for the Blog and its content.</a> [/section]\n\n[section title="Our Bloggers"]<a href="/?cat_ID=127">Find the list of the Bloggers who have accepted to share their vision and all their articles.</a>[/section]\n\n[section title="Partners"]<a href="/?page_id=2053">Find the list of institutions that support and work with us towards a better situation for children in DRC.</a>[/section]\n\n[section title="Volunteers"]<a href="/?page_id=2061">They volunteer to help us manage this Blog and make it interactive and easy to navigate.</a>[/section]\n\n[section title="Contact us"] <a href="/?page_id=1895">All information needed to contact us.</a>[/section]\n\n[section title="Follow us!"]<a href="/?page_id=2000"> </a><a href="/?page_id=2000">Stay informed on UNICEF''s work in DRC and how to support us</a>.[/section]\n\n[/accordion] [/one_half_last]', 'The Team', '', 'publish', 'open', 'open', '', 'the-team', '', '', '2014-01-14 17:33:43', '2014-01-14 17:33:43', '', 0, 'http://ponabana.com/?page_id=3781', 0, 'page', '', 0),
(3990, 1, '2014-01-21 10:33:11', '2014-01-21 10:33:11', '<a href="http://ponabana.com/wp-content/uploads/2013/11/unicef.png"><img class="size-full wp-image-2038 aligncenter" alt="unicef" src="http://ponabana.com/wp-content/uploads/2013/11/unicef.png" width="151" height="37" /></a>\n\n&nbsp;\n<table class=" aligncenter" width="562" border="0" cellspacing="0" cellpadding="0"><colgroup> <col width="251" /> <col width="131" /> <col width="108" /> <col width="72" /> </colgroup>\n<tbody>\n<tr>\n<td width="251" height="21"><strong>Objet</strong></td>\n<td width="131"><strong>Date de lancement</strong></td>\n<td width="108"><strong>Date de clôture</strong></td>\n<td width="72"></td>\n</tr>\n<tr>\n<td width="251" height="21"></td>\n<td width="131"></td>\n<td width="108"></td>\n<td width="72"></td>\n</tr>\n<tr>\n<td width="251" height="21"></td>\n<td width="131"></td>\n<td width="108"></td>\n<td width="72"></td>\n</tr>\n<tr>\n<td width="251" height="21"></td>\n<td width="131"></td>\n<td width="108"></td>\n<td width="72"></td>\n</tr>\n<tr>\n<td width="251" height="21"></td>\n<td width="131"></td>\n<td width="108"></td>\n<td width="72"></td>\n</tr>\n<tr>\n<td width="251" height="21"></td>\n<td width="131"></td>\n<td width="108"></td>\n<td width="72"></td>\n</tr>\n<tr>\n<td width="251" height="21"></td>\n<td width="131"></td>\n<td width="108"></td>\n<td width="72"></td>\n</tr>\n<tr>\n<td width="251" height="21"></td>\n<td width="131"></td>\n<td width="108"></td>\n<td width="72"></td>\n</tr>\n<tr>\n<td width="251" height="21"></td>\n<td width="131"></td>\n<td width="108"></td>\n<td width="72"></td>\n</tr>\n<tr>\n<td width="251" height="21"></td>\n<td width="131"></td>\n<td width="108"></td>\n<td width="72"></td>\n</tr>\n<tr>\n<td width="251" height="21"></td>\n<td width="131"></td>\n<td width="108"></td>\n<td width="72"></td>\n</tr>\n<tr>\n<td width="251" height="21"></td>\n<td width="131"></td>\n<td width="108"></td>\n<td width="72"></td>\n</tr>\n</tbody>\n</table>', 'Nos appels